import { Component } from '@angular/core';

@Component({
  selector: 'app-page3',
  templateUrl: './page3.component.html',
  styles: [
  ]
})
export class Page3Component {
  classes="big blue";
}
<style>
    .big {
        font-weight: bold;
        font-size: x-large;
    }

    .small {
        font-weight: normal;
        font-size: x-small;
        background-color: yellow
    }

    .blue {
        color: blue
    }

    .green {
        color: green
    }
</style>
<div class="big blue">Hello</div>
<div [class]="classes" >Hello1</div>
<div [class.small]="true">Hello2</div>
