import { Component } from '@angular/core';
import { MeetingRoomService } from '../shared/meeting-room.service';
import { NgForm } from '@angular/forms';
import { MeetingRoom } from '../shared/meeting-room';

@Component({
  selector: 'app-meeting-room-detail',
  templateUrl: './meeting-room-detail.component.html',
  styles: [
  ]
})
export class MeetingRoomDetailComponent {
  constructor(public service: MeetingRoomService) { }
  public capacity: any[] = [
    { value: 5, text: "5人" }, { value: 10, text: "10人" },
    { value: 15, text: "15人" }, { value: 20, text: "20人" },
    { value: 30, text: "30人" }, { value: 40, text: "40人" },
    { value: 50, text: "50人" }, { value: 100, text: "100人" },
    { value: 200, text: "200人" }];
 ngOnInit() {
    this.resetForm();
  }
  resetForm(form?: NgForm) {
    if (form != null)
      form.form.reset();
    this.service.formData = new MeetingRoom();
  }

}
<form #form="ngForm" autocomplete="off">
    <div class="input-group mb-3">
        <i class="input-group-text fa-solid fa-people-roof"></i>
        <input name="name" class="form-control" #name="ngModel"
        [(ngModel)]="service.formData.name"
 placeholder="會議室名稱" required >
    </div>
    <div class="input-group mb-3">
        <i class="input-group-text fa-solid fa-users"></i>
        <select name="size" class="form-control" required #size="ngModel" [(ngModel)]="service.formData.size">
            <option [ngValue]="0">--- 請選擇會議室容納人次 ---</option>
            <option *ngFor="let cap of this.capacity"  [ngValue]="cap.value">
                {{cap.text}}</option>
        </select>
    </div>
    <div class="input-group mb-3">
        <i class="input-group-text fa-solid fa-video"></i>
        <label class="form-control">
            <input type="checkbox" aria-label="Checkbox for following text input" name="projector" #projector="ngModel"
                [(ngModel)]="service.formData.projector">
            投影機
        </label>
    </div>
    <div class="input-group mb-3">
        <i class="input-group-text fa-solid fa-tv"></i>
        <label class="form-control">
            <input type="checkbox" aria-label="Checkbox for following text input" name="tv" #tv="ngModel"
                [(ngModel)]="service.formData.tv">
            電視
        </label>
    </div>
    <div class="d-grid gap-2">
        <button class="btn btn-success btn-lg btn-block" type="submit" [disabled]="form.invalid">
            <i class="fa-solid fa-database"></i> Submit
        </button>
    </div>
</form>
