import { Component } from '@angular/core';

@Component({
  selector: 'app-page4',
  templateUrl: './page4.component.html',
  styles: [
  ]
})
export class Page4Component {
  SayHi() {
    alert("Hello");
  }

  onClick(element: string) {
    console.log(element);
  }

  onClick2(event: Event, element: string) {
    console.log(element);
    console.log(event.target);
    console.log(event.currentTarget);
  }


  onClick3(event: Event, element:string){    
    console.log(element);
    console.log(event.target);
    console.log(event.currentTarget);
    // event.preventDefault();
    // event.stopPropagation();    
  }

  result: string = "0102";
  onchange(event: Event){
    let selectElement : HTMLSelectElement = <HTMLSelectElement>event.target;
    console.log(selectElement.value);
    this.result = selectElement.value;
  }

}
<p>page4 works!</p>

<div>
    你選的是: {{result}} 
    <select (change)="onchange($event)" [value]="result">     
      <option value="0101">Java Script</option>
      <option value="0102">Type Script</option>
      <option value="0103">Angular</option>
    </select>
</div>


<div (click)="onClick3($event, 'div')">
    <h3>$event methods</h3>
    <a (click)="onClick3($event, 'a')" href="http://www.google.com">Google</a>
</div>

<div (click)="onClick2($event, 'div')">
    <h3>$event</h3>
    <button (click)="onClick2($event, 'button') ">
         Button</button>
  </div>



<div (click)="onClick('div')">
	<button (click)="onClick('button') ">
		Button</button>
</div>



<button (click)="SayHi()">sayHi</button>
<button on-click="SayHi()">sayHi</button>
