import { Component } from '@angular/core';
import { routes } from './app-routing.module';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styles: []
})
export class AppComponent {
  title = 'mod04';
  routeList = routes;
  mySearch: string = ""; 
  isTouch: boolean = false;
  isFound: boolean = false;
  resultStyles= {};
  onTextChange(search:string) {
    this.isTouch = true;
    this.isFound = search.length > 3;
    this.resultStyles = {
      'border': this.isFound?'solid 3px blue':'solid 3px red',
      'color': this.isFound ? 'black' : 'red',
      'background-color': this.isFound ? 'yellow' : 'white'
    };
  }

}
<style>
  ul li {
    display: inline;
  }

  ul li a {
    padding: 0 5px
  }
</style>

<div style="text-align:center">
  <h1>
    Welcome to {{ title }}!
  </h1>

  <ul>
    <li *ngFor="let r of routeList">
      <a [routerLink]="[r.path]">{{r.title}}</a>
    </li>
  </ul>

</div>
