import { Component } from '@angular/core';
import { routes } from './app-routing.module';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styles: []
})
export class AppComponent {
  title = 'mod04';
  routeList = routes;
  mySearch: string="hi";
}
<style>
  ul li {
    display: inline;
  }

  ul li a {
    padding: 0 5px
  }
</style>

<div style="text-align:center">
  <h1>
    Welcome to {{ title }}!
  </h1>

  <ul>
    <li *ngFor="let r of routeList">
      <a [routerLink]="[r.path]">{{r.title}}</a>
    </li>
  </ul>

</div>
<searchBox [searchText]="mySearch" (searchTextChange)="mySearch=$event"></searchBox>

<hr />
<router-outlet></router-outlet>
