import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styles: []
})
export class AppComponent {
  QTY : number =12;
  Stock: number =10;
  title = '購物車';

}
<h1>{{title}}</h1>
購買數量:
<input type="number" [(ngModel)]="QTY">
<CheckStock [QTY]="QTY" [Stock]="Stock">
  <span #span class="lower">庫存不足，目前只有 {{Stock}}
  </span>
  <span #span class="enough">目前庫存量足夠!</span>

</CheckStock>
